contracts/interfaces/ISequencerInfo.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

interface ISequencerInfo {
    error OwnedSequencer();
    error SignerExisted();
    error NoSuchSeq();
    error NullAddress();
    error SeqNotActive();
    error NotSeqOwner();
    error NotSeqSigner();
    error NoRewardRecipient();
    error NotWhitelisted();

    // the sequencer status
    enum Status {
        Unavailabe, // placeholder for default value
        Inactive, // the sequencer will be Inactive if its owner starts unlock
        Active, // the sequencer is active when it locks tokens on the contract
        Unlocked // Exited, the sequencer has no locked tokens, and it's no longer produce blocks on L2
    }

    struct Sequencer {
        uint256 amount; // sequencer current locked
        uint256 reward; // sequencer current reward that have not claimed
        uint256 activationBatch; // sequencer activation batch id
        uint256 updatedBatch; // batch id of the last updated
        uint256 deactivationBatch; // sequencer deactivation batch id
        uint256 deactivationTime; // sequencer deactivation timestamp
        uint256 unlockClaimTime; // timestamp that sequencer can claim unlocked token, it's equal to deactivationTime + WITHDRAWAL_DELAY
        uint256 nonce; // sequencer operations number, starts from 1, and used internally by the Metis consensus client
        address owner; // the operator address, owns this sequencer node, it controls lock/relock/unlock/claim functions
        address signer; // sequencer signer, an address to sign L2 blocks, if you want to update it, you must have the privkey of this address
        bytes pubkey; // sequencer signer pubkey
        address rewardRecipient; // sequencer rewarder recipient address
        Status status; // sequencer status
    }

    /**
     * @dev Emitted if owner call 'setWhitelist'
     * @param _user the address who can lock token
     * @param _yes white address state
     */
    event SetWhitelist(address _user, bool _yes);

    /**
     * @dev Emitted when reward recipient address update in 'setSequencerRewardRecipient'
     * @param _seqId the sequencerId
     * @param _recipient the address receive reward token
     */
    event SequencerRewardRecipientChanged(uint256 _seqId, address _recipient);

    /**
     * @dev Emitted when sequencer owner is changed
     * @param _seqId the sequencerId
     * @param _owner the sequencer owner
     */
    event SequencerOwnerChanged(uint256 _seqId, address _owner);

    function seqOwners(address owner) external returns (uint256 seqId);
}

contracts/LockingInfo.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

import {IL1ERC20Bridge} from "./interfaces/IL1ERC20Bridge.sol";
import {ILockingInfo} from "./interfaces/ILockingInfo.sol";
import {ISequencerInfo} from "./interfaces/ISequencerInfo.sol";

contract LockingInfo is ILockingInfo, OwnableUpgradeable {
    using SafeERC20 for IERC20;

    address public bridge; // L1 metis bridge address
    address public l1Token; // L1 metis token address
    address public l2Token; // L2 metis token address
    uint256 public l2ChainId; // L2 metis chainId

    uint256 public minLock; // min lock amount
    uint256 public maxLock; // max lock amount

    uint256 public totalLocked; // the total locked amount
    uint256 public totalRewardsLiquidated;

    // Locking pool address
    address public manager;

    // the reward payer
    address public rewardPayer;

    modifier OnlyManager() {
        require(msg.sender == manager, "Not manager");
        _;
    }

    function initialize(
        address _bridge,
        address _l1Token,
        address _l2Token,
        uint256 _l2ChainId
    ) external initializer {
        bridge = _bridge;
        l1Token = _l1Token;
        l2Token = _l2Token;
        l2ChainId = _l2ChainId;

        minLock = 20_000 ether;
        maxLock = 100_000 ether;

        __Ownable_init();
    }

    function initManager(address _manager) external onlyOwner {
        require(manager == address(0), "manager has been initialized");
        manager = _manager;
    }

    /**
     * @dev updateMinAmounts Allow owner to update min lock amount
     * @param _minLock new min lock amount
     */
    function setMinLock(uint256 _minLock) external onlyOwner {
        require(_minLock > 0, "_minLock=0");
        minLock = _minLock;
        emit SetMinLock(_minLock);
    }

    /**
     * @dev setMaxLock Allow owner to update max lock amount
     * @param _maxLock new max lock amount
     */
    function setMaxLock(uint256 _maxLock) external onlyOwner {
        require(_maxLock >= minLock, "maxLock<minLock");
        maxLock = _maxLock;
        emit SetMaxLock(_maxLock);
    }

    /**
     * @dev setRewardPayer update reward payer address by owner
     * @param _payer new reward payer
     */
    function setRewardPayer(address _payer) external onlyOwner {
        rewardPayer = _payer;
        emit SetRewardPayer(_payer);
    }

    /**
     * @dev newSequencer register a new sequencer, it can only be called from manager contract
     * @param _id the sequencer id
     * @param _owner the sequencer owenr
     * @param _signer the sequencer node address
     * @param _amount the amount to lock in
     * @param _batchId current batch id
     * @param _signerPubkey the sequencer public key
     */
    function newSequencer(
        uint256 _id,
        address _owner,
        address _signer,
        uint256 _amount,
        uint256 _batchId,
        bytes calldata _signerPubkey
    ) external override OnlyManager {
        require(_amount >= minLock && _amount <= maxLock, "invalid amount");

        // use local variable to save gas
        uint256 _tatalLocked = totalLocked + _amount;
        totalLocked = _tatalLocked;

        IERC20(l1Token).safeTransferFrom(_owner, address(this), _amount);
        emit Locked(
            _signer,
            _id,
            1, // nocne starts from 1 for a new sequencer
            _batchId,
            _amount,
            _tatalLocked,
            _signerPubkey
        );
    }

    /**
     * @dev increaseLocked lock tokens to the sequencer, it can only be called from manager contract
     * @param _seqId the sequencer id
     * @param _nonce the sequencer nonce
     * @param _owner the sequencer owner address
     * @param _locked the locked amount of the sequencer at last
     * @param _incoming amount from current transaction
     * @param _fromReward use reward to lock
     */
    function increaseLocked(
        uint256 _seqId,
        uint256 _nonce,
        address _owner,
        uint256 _locked,
        uint256 _incoming,
        uint256 _fromReward
    ) external override OnlyManager {
        require(_locked <= maxLock, "locked>maxLock");

        // get increased number and transfer it into escrow
        uint256 increased = _incoming + _fromReward;
        require(increased > 0, "No new locked added");
        IERC20(l1Token).safeTransferFrom(_owner, address(this), _incoming);

        // get current total locked and emit event
        uint256 _totalLocked = totalLocked + increased;
        totalLocked = _totalLocked;

        emit Relocked(_seqId, increased, totalLocked);
        emit LockUpdate(_seqId, _nonce, _locked);
    }

    /**
     * @dev initializeUnlock the first step to unlock
     *      current reward will be distributed
     * @param _seqId the sequencer id
     * @param _reward the reward to withdraw
     * @param _seq the current sequencer state
     * @param _l2gas the l2gas for L1bridge
     */
    function initializeUnlock(
        uint256 _seqId,
        uint256 _reward,
        uint32 _l2gas,
        ISequencerInfo.Sequencer calldata _seq
    ) external payable override OnlyManager {
        _liquidateReward(_seqId, _reward, _seq.rewardRecipient, _l2gas);
        emit UnlockInit(
            _seq.signer,
            _seqId,
            _seq.nonce,
            _seq.deactivationBatch,
            _seq.deactivationTime,
            _seq.unlockClaimTime,
            _reward
        );
    }

    /**
     * @dev finalizeUnlock the last step to unlock
     * @param _operator the sequencer id
     * @param _seqId the sequencer id
     * @param _amount locked amount
     * @param _reward reward amount
     * @param _recipient recipient
     * @param _l2gas the l2gas for L1bridge
     */
    function finalizeUnlock(
        address _operator,
        uint256 _seqId,
        uint256 _amount,
        uint256 _reward,
        address _recipient,
        uint32 _l2gas
    ) external payable OnlyManager {
        // update totalLocked value
        uint256 _tatalLocked = totalLocked - _amount;
        totalLocked = _tatalLocked;

        IERC20(l1Token).safeTransfer(_operator, _amount);
        if (_reward > 0) {
            _liquidateReward(_seqId, _reward, _recipient, _l2gas);
        }
        emit Unlocked(_operator, _seqId, _amount, _tatalLocked);
    }

    function liquidateReward(
        uint256 _seqId,
        uint256 _amount,
        address _recipient,
        uint32 _l2gas
    ) external payable override OnlyManager {
        _liquidateReward(_seqId, _amount, _recipient, _l2gas);
    }

    /**
     * @dev distributeReward reward distribution
     * @param _batchId The batchId that submitted the reward is that
     */
    function distributeReward(
        uint256 _batchId,
        uint256 _totalReward
    ) external OnlyManager {
        // reward income
        IERC20(l1Token).safeTransferFrom(
            rewardPayer,
            address(this),
            _totalReward
        );
        emit BatchSubmitReward(_batchId);
    }

    /**
     * @dev logSignerChange log event SignerChange
     */
    function logSignerChange(
        uint256 sequencerId,
        address oldSigner,
        address newSigner,
        uint256 nonce,
        bytes calldata signerPubkey
    ) external OnlyManager {
        emit SignerChange(
            sequencerId,
            nonce,
            oldSigner,
            newSigner,
            signerPubkey
        );
    }

    function _liquidateReward(
        uint256 _seqId,
        uint256 _amount,
        address _recipient,
        uint32 _l2gas
    ) internal {
        _bridgeTo(_recipient, _amount, _l2gas);
        uint256 total = totalRewardsLiquidated + _amount;
        totalRewardsLiquidated = total;
        emit ClaimRewards(_seqId, _recipient, _amount, total);
    }

    function _bridgeTo(
        address _recipient,
        uint256 _amount,
        uint32 _l2gas
    ) internal {
        if (_amount == 0) {
            return;
        }

        IERC20(l1Token).safeIncreaseAllowance(bridge, _amount);
        IL1ERC20Bridge(bridge).depositERC20ToByChainId{value: msg.value}(
            l2ChainId,
            l1Token,
            l2Token,
            _recipient,
            _amount,
            _l2gas,
            ""
        );
    }
}

contracts/LockingPool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

import {PausableUpgradeable} from "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";

import {ILockingInfo} from "./interfaces/ILockingInfo.sol";
import {ILockingPool} from "./interfaces/ILockingPool.sol";

import {SequencerInfo} from "./SequencerInfo.sol";

contract LockingPool is ILockingPool, PausableUpgradeable, SequencerInfo {
    struct BatchState {
        uint256 id; // current batch id
        uint256 number; // L1 block number when current batch is submitted
        uint256 startEpoch; // start epoch number for current batch
        uint256 endEpoch; // end epoch number for current batch
    }

    ILockingInfo public escrow;

    // delay time for unlock
    uint256 public exitDelayPeriod;

    // reward per L2 block
    uint256 public rewardPerBlock;

    // the mpc address
    address public mpcAddress;

    // current batch state
    BatchState public curBatchState;

    // the number of batch that signer can be updated since the last update
    uint256 public signerUpdateThrottle;

    function initialize(address _escrow) external initializer {
        exitDelayPeriod = 21 days;
        rewardPerBlock = 761000 gwei;

        // init batch state, default starts from 1
        curBatchState = BatchState({
            id: 1,
            number: block.number,
            startEpoch: 0,
            endEpoch: 0
        });

        escrow = ILockingInfo(_escrow);

        signerUpdateThrottle = 1;

        __Pausable_init();
        __LockingBadge_init();
    }

    /**
     * @dev currentBatch returns current batch id
     */
    function currentBatch() external view returns (uint256) {
        return curBatchState.id;
    }

    /**
     * @dev WITHDRAWAL_DELAY returns the period
     *      just for compatibility, this ambiguous func is added
     */
    function WITHDRAWAL_DELAY() external view returns (uint256) {
        return exitDelayPeriod;
    }

    /**
     * @dev BLOCK_REWARD returns the current reward per block
     *      just for compatibility, this ambiguous func is added
     */
    function BLOCK_REWARD() external view returns (uint256) {
        return rewardPerBlock;
    }

    /**
     * @dev updateMpc update the mpc address
     * @param _newMpc new mpc address
     */
    function updateMpc(address _newMpc) external onlyOwner {
        mpcAddress = _newMpc;
        emit UpdateMpc(_newMpc);
    }

    /**
     * @dev setPause
     * @param _yes pause or not
     */
    function setPause(bool _yes) external onlyOwner {
        if (_yes) {
            _pause();
        } else {
            _unpause();
        }
    }

    /**
     *  @dev updateWithdrawDelayTimeValue Allow owner to set withdraw delay time.
     *  @param _time new withdraw delay time
     */
    function updateWithdrawDelayTimeValue(uint256 _time) external onlyOwner {
        require(_time > 0, "dalayTime==0");
        uint256 pre = exitDelayPeriod;
        exitDelayPeriod = _time;
        emit WithrawDelayTimeChange(_time, pre);
    }

    /**
     * @dev updateBlockReward  Allow owner to set per block reward
     * @param newReward the block reward
     */
    function updateBlockReward(uint256 newReward) external onlyOwner {
        require(newReward != 0, "invalid newReward");
        uint256 pre = rewardPerBlock;
        rewardPerBlock = newReward;
        emit RewardUpdate(newReward, pre);
    }

    /**
     * @dev setSignerUpdateThrottle  set signerUpdateThrottle
     * @param _n the new value of the throttle
     *        Note: it can be 0
     */
    function setSignerUpdateThrottle(uint256 _n) external onlyOwner {
        signerUpdateThrottle = _n;
        emit SetSignerUpdateThrottle(_n);
    }

    /**
     * @dev updateSigner Allow sqeuencer to update new signers to replace old signer addressesand NFT holder will be transfer driectly
     * @param _seqId the sequencer id
     * @param _signerPubkey the new signer pubkey address
     */
    function updateSigner(
        uint256 _seqId,
        bytes calldata _signerPubkey
    ) external whitelistRequired {
        Sequencer storage seq = sequencers[_seqId];
        if (seq.status != Status.Active) {
            revert SeqNotActive();
        }

        // can be updated by the signer
        address signer = seq.signer;
        if (signer != msg.sender) {
            revert NotSeqSigner();
        }

        require(
            curBatchState.id >= seq.updatedBatch + signerUpdateThrottle,
            "signer updating throttle"
        );

        address newSigner = _getAddrByPubkey(_signerPubkey);
        // the new signer should not be a signer before
        if (seqSigners[newSigner] != 0) {
            revert SignerExisted();
        }
        seq.pubkey = _signerPubkey;

        seq.signer = newSigner;
        seqSigners[newSigner] = _seqId;

        // the previous signer address can'be used again
        _invalidSignerAddress(signer);

        // set signer updated batch id
        seq.updatedBatch = curBatchState.id;

        uint256 nonce = seq.nonce + 1;
        seq.nonce = nonce;
        // the event emits in LocingInfo is just for compatibility
        escrow.logSignerChange(_seqId, signer, newSigner, nonce, _signerPubkey);
    }

    /**
     * @dev lockFor lock Metis and participate in the sequencer node
     *      the msg.sender will be owner of the sequencer
     *      the owner has abilities to leverage lock/relock/unlock/cliam
     *      **Note**: the locking amount will be trasnfered from msg.sender
     *      and you need to approve the Metis of msg.sender to **LockingInfo** contract
     *      instead of this LockingPool contract
     *
     *      the default reward recipient is an empty address
     *      you need to update it using setSequencerRewardRecipient afterward
     * @param _signer Sequencer signer address
     * @param _amount Amount of L1 metis token to lock for.
     * @param _signerPubkey Sequencer signer pubkey, it should be uncompressed
     */
    function lockFor(
        address _signer,
        uint256 _amount,
        bytes calldata _signerPubkey
    ) external whenNotPaused whitelistRequired {
        uint256 batchId = curBatchState.id;
        address owner = msg.sender;
        uint256 seqId = _lockFor(
            batchId,
            owner,
            _signer,
            _signerPubkey,
            _amount,
            address(0)
        );
        escrow.newSequencer(
            seqId,
            owner,
            _signer,
            _amount,
            batchId,
            _signerPubkey
        );
        emit SequencerOwnerChanged(seqId, msg.sender);
        emit SequencerRewardRecipientChanged(seqId, address(0));
    }

    /**
     * @dev lockWithRewardRecipient is the same with lockFor, but you can provide a reward receipent
     * @param _signer Sequencer signer address
     * @param _rewardRecipient Sequencer reward receiptent
     *        you can use an empty address if you haven't choosed an address
     *        and update it using `setSequencerRewardRecipient` after then
     * @param _amount Amount of L1 metis token to lock for.
     * @param _signerPubkey Sequencer signer pubkey
     *         it should be uncompressed and matched with signer address
     */
    function lockWithRewardRecipient(
        address _signer,
        address _rewardRecipient,
        uint256 _amount,
        bytes calldata _signerPubkey
    ) external whenNotPaused whitelistRequired {
        uint256 batchId = curBatchState.id;
        uint256 seqId = _lockFor(
            batchId,
            msg.sender,
            _signer,
            _signerPubkey,
            _amount,
            _rewardRecipient
        );
        escrow.newSequencer(
            seqId,
            msg.sender,
            _signer,
            _amount,
            batchId,
            _signerPubkey
        );
        emit SequencerOwnerChanged(seqId, msg.sender);
        emit SequencerRewardRecipientChanged(seqId, _rewardRecipient);
    }

    /**
     * @dev relock allow sequencer operator to increase the amount of locked positions
     * @param _seqId the id of your sequencer
     * @param _amount amount of token to relock, it can be 0 if you want to relock your rewrad
     * @param _lockReward use true if lock the current rewards
     */
    function relock(
        uint256 _seqId,
        uint256 _amount,
        bool _lockReward
    ) external whenNotPaused whitelistRequired {
        Sequencer storage seq = sequencers[_seqId];
        if (seq.status != Status.Active) {
            revert SeqNotActive();
        }

        if (seq.owner != msg.sender) {
            revert NotSeqOwner();
        }

        uint256 _fromReward = 0;
        if (_lockReward) {
            _fromReward = seq.reward;
            seq.reward = 0;
        }

        uint256 locked = seq.amount + _amount + _fromReward;
        uint256 nonce = seq.nonce + 1;

        seq.nonce = nonce;
        seq.amount = locked;

        escrow.increaseLocked(
            _seqId,
            nonce,
            msg.sender,
            locked,
            _amount,
            _fromReward
        );
    }

    /**
     * @dev unlock your metis and exit the sequencer node
     *      the reward will be arrived by L1Bridge first
     *      and you need to wait the exit period and call
     *      unlockClaim to cliam your locked token
     * @param _seqId sequencer id
     * @param _l2Gas the L2 gas limit for L1Bridge.
     *       the reward is distributed by bridge
     *       so you need to pay the ETH as the bridge fee
     */
    function unlock(
        uint256 _seqId,
        uint32 _l2Gas
    ) external payable whenNotPaused whitelistRequired {
        _unlock(_seqId, false, _l2Gas);
    }

    /**
     * @dev forceUnlock Allow owner to force a sequencer node to exit
     * @param _seqId the sequencer id
     * @param _l2Gas l2 gas limit, see above for the detail
     */
    function forceUnlock(
        uint256 _seqId,
        uint32 _l2Gas
    ) external payable onlyOwner {
        _unlock(_seqId, true, _l2Gas);
    }

    /**
     * @dev unlockClaim claim your locked tokens after the waiting period is passed
     *      l2 fee is required if you have unclaimed reward
     *
     * @param _seqId sequencer id
     * @param _l2Gas l2 gas limit
     */
    function unlockClaim(
        uint256 _seqId,
        uint32 _l2Gas
    ) external payable whenNotPaused whitelistRequired {
        Sequencer storage seq = sequencers[_seqId];
        if (seq.owner != msg.sender) {
            revert NotSeqOwner();
        }

        address recipient = seq.rewardRecipient;
        if (recipient == address(0)) {
            revert NoRewardRecipient();
        }

        // operator can only claim after WITHDRAWAL_DELAY
        require(
            seq.status == Status.Inactive &&
                seq.unlockClaimTime <= block.timestamp,
            "Not allowed to cliam"
        );

        uint256 amount = seq.amount;
        uint256 reward = seq.reward;

        seq.amount = 0;
        seq.reward = 0;
        seq.nonce++;
        seq.status = Status.Unlocked;
        seqStatuses[Status.Inactive]--;
        seqStatuses[Status.Unlocked]++;

        delete seqOwners[seq.owner];

        // invalid it
        _invalidSignerAddress(seq.signer);

        escrow.finalizeUnlock{value: msg.value}(
            msg.sender,
            _seqId,
            amount,
            reward,
            recipient,
            _l2Gas
        );
    }

    /**
     * @dev withdrawRewards withdraw current rewards
     *      l2 fee is required
     *
     * @param _seqId unique integer to identify a sequencer.
     * @param _l2Gas bridge reward to L2 gasLimit
     */
    function withdrawRewards(
        uint256 _seqId,
        uint32 _l2Gas
    ) external payable whenNotPaused whitelistRequired {
        Sequencer storage seq = sequencers[_seqId];
        if (seq.status != Status.Active) {
            revert SeqNotActive();
        }

        if (seq.owner != msg.sender) {
            revert NotSeqOwner();
        }

        address recipient = seq.rewardRecipient;
        if (recipient == address(0)) {
            revert NoRewardRecipient();
        }
        uint256 reward = seq.reward;
        if (reward > 0) {
            seq.reward = 0;
            escrow.liquidateReward{value: msg.value}(
                _seqId,
                reward,
                recipient,
                _l2Gas
            );
        }
    }

    /**
     * @dev batchSubmitRewards Allow to submit L2 sequencer block information, and attach Metis reward tokens for reward distribution
     * @param _batchId The batchId that submitted the reward is that
     * @param _startEpoch The startEpoch that submitted the reward is that
     * @param _endEpoch The endEpoch that submitted the reward is that
     * @param _seqs Those sequencers can receive rewards
     * @param _blocks How many blocks each sequencer finished.
     */
    function batchSubmitRewards(
        uint256 _batchId,
        uint256 _startEpoch,
        uint256 _endEpoch,
        address[] calldata _seqs,
        uint256[] calldata _blocks
    ) external {
        require(msg.sender == mpcAddress, "not MPC");
        require(
            _seqs.length == _blocks.length && _seqs.length > 0,
            "mismatch length"
        );

        BatchState storage bs = curBatchState;
        uint256 nextBatch = bs.id + 1;
        require(nextBatch == _batchId, "invalid batch id");
        bs.id = nextBatch;

        require(bs.endEpoch + 1 == _startEpoch, "invalid startEpoch");
        require(_startEpoch < _endEpoch, "invalid endEpoch");

        uint256 rpb = rewardPerBlock;
        uint256 totalReward = 0;
        for (uint256 i = 0; i < _seqs.length; i++) {
            uint256 reward = _blocks[i] * rpb;
            uint256 seqId = seqSigners[_seqs[i]];
            Sequencer storage seq = sequencers[seqId];
            if (seq.status == Status.Unavailabe) {
                revert NoSuchSeq();
            }
            seq.reward += reward;
            totalReward += reward;
        }
        bs.number = block.number;
        bs.startEpoch = _startEpoch;
        bs.endEpoch = _endEpoch;
        escrow.distributeReward(_batchId, totalReward);
        emit DistributeReward(
            _batchId,
            _startEpoch,
            _endEpoch,
            totalReward,
            rpb
        );
    }

    function _unlock(uint256 _seqId, bool _force, uint32 _l2Gas) internal {
        Sequencer storage seq = sequencers[_seqId];
        if (seq.status != Status.Active) {
            revert SeqNotActive();
        }

        uint256 actived = --seqStatuses[Status.Active];
        uint256 inactived = ++seqStatuses[Status.Inactive];

        if (!_force) {
            if (seq.owner != msg.sender) {
                revert NotSeqOwner();
            }

            // BFT check, actived sequencer count must be high than 2/3 of total
            if (inactived * 3 > actived + inactived) {
                revert("BFT restriction");
            }
        }

        address recipient = seq.rewardRecipient;
        if (recipient == address(0)) {
            revert NoRewardRecipient();
        }

        seq.status = Status.Inactive;
        seq.deactivationBatch = curBatchState.id;
        seq.deactivationTime = block.timestamp;
        seq.unlockClaimTime = block.timestamp + exitDelayPeriod;
        seq.nonce++;

        uint256 unclaimed = seq.reward;
        seq.reward = 0;

        escrow.initializeUnlock{value: msg.value}(
            _seqId,
            unclaimed,
            _l2Gas,
            seq
        );
    }
}

contracts/SequencerInfo.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {ISequencerInfo} from "./interfaces/ISequencerInfo.sol";

contract SequencerInfo is OwnableUpgradeable, ISequencerInfo {
    uint256 public totalSequencers;

    // whitelist
    mapping(address owner => bool yes) public whitelist;

    // sequencerId => sequencer
    mapping(uint256 seqId => Sequencer _seq) public sequencers;

    // sequencer owner address => sequencerId
    // Note: sequencerId starts from 1
    // sequencer does not exist if the seqId is 0
    mapping(address owner => uint256 seqId) public seqOwners;

    // sequencer signer address => sequencerId
    // the signer can't be reused afterward if the sequencer exits or updates its pubkey
    // It means that the signer is invalid if the seqId is type(uint256).max
    mapping(address signer => uint256 seqId) public seqSigners;

    // sequencer status => count
    mapping(Status status => uint256 count) public seqStatuses;

    /**
     * @dev Modifier to make a function callable only the msg.sender is in the whitelist.
     */
    modifier whitelistRequired() {
        if (!whitelist[msg.sender]) {
            revert NotWhitelisted();
        }
        _;
    }

    function __LockingBadge_init() internal {
        __Ownable_init();
    }

    /**
     * @dev setWhitelist Allow owner to update white address list
     * @param _addr the address who can lock token
     * @param _yes white address state
     */
    function setWhitelist(address _addr, bool _yes) external onlyOwner {
        whitelist[_addr] = _yes;
        emit SetWhitelist(_addr, _yes);
    }

    /**
     * @dev setSequencerRewardRecipient Allow sequencer owner to set a reward recipient
     * @param _seqId The sequencerId
     * @param _recipient Who will receive the reward token
     */
    function setSequencerRewardRecipient(
        uint256 _seqId,
        address _recipient
    ) external whitelistRequired {
        Sequencer storage seq = sequencers[_seqId];

        if (seq.owner != msg.sender) {
            revert NotSeqOwner();
        }

        if (seq.status != Status.Active) {
            revert SeqNotActive();
        }

        if (_recipient == address(0)) {
            revert NullAddress();
        }

        seq.rewardRecipient = _recipient;
        emit SequencerRewardRecipientChanged(_seqId, _recipient);
    }

    /**
     * @dev setSequencerOwner update sequencer owner
     * @param _seqId The sequencerId
     * @param _owner the new owner
     */
    function setSequencerOwner(
        uint256 _seqId,
        address _owner
    ) external whitelistRequired {
        if (_owner == address(0)) {
            revert NullAddress();
        }

        Sequencer storage seq = sequencers[_seqId];
        if (seq.status != Status.Active) {
            revert SeqNotActive();
        }

        address owner = seq.owner;
        if (owner != msg.sender) {
            revert NotSeqOwner();
        }
        seq.owner = _owner;
        delete seqOwners[owner];
        seqOwners[_owner] = _seqId;
        emit SequencerOwnerChanged(_seqId, _owner);
    }

    function _lockFor(
        uint256 _batchId,
        address _owner,
        address _signer,
        bytes calldata _signerPubkey,
        uint256 _amount,
        address _rewardRecipient
    ) internal returns (uint256 _seqId) {
        // it will check the _signer must not be empty address
        require(
            _getAddrByPubkey(_signerPubkey) == _signer,
            "pubkey and address mismatch"
        );

        if (seqOwners[_owner] != 0) {
            revert OwnedSequencer();
        }

        if (seqSigners[_signer] != 0) {
            revert SignerExisted();
        }

        uint256 seqs = totalSequencers;

        // seqId starts from 1
        _seqId = seqs + 1;

        seqOwners[_owner] = _seqId;
        seqSigners[_signer] = _seqId;
        seqStatuses[Status.Active]++;
        totalSequencers = _seqId;

        sequencers[_seqId] = Sequencer({
            amount: _amount,
            reward: 0,
            activationBatch: _batchId,
            deactivationBatch: 0,
            updatedBatch: _batchId,
            deactivationTime: 0,
            unlockClaimTime: 0,
            nonce: 1,
            owner: msg.sender,
            signer: _signer,
            pubkey: _signerPubkey,
            rewardRecipient: _rewardRecipient,
            status: Status.Active
        });
        return _seqId;
    }

    function _getAddrByPubkey(
        bytes calldata _signerPubkey
    ) internal pure returns (address) {
        require(_signerPubkey.length == 64, "invalid pubkey");
        address newSigner = address(uint160(uint256(keccak256(_signerPubkey))));
        require(newSigner != address(0), "empty address");
        return newSigner;
    }

    function _invalidSignerAddress(address _signer) internal {
        seqSigners[_signer] = type(uint256).max;
    }

    /**
     * @dev This empty reserved space is put in place to allow future versions to add new
     * variables without shifting down storage in the inheritance chain.
     * See https://docs.openzeppelin.com/contracts/4.x/upgradeable#storage_gaps
     */
    uint256[50] private __gap;
}

contracts/test/TestBridge.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

import {IL1ERC20Bridge} from "../interfaces/IL1ERC20Bridge.sol";

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract TestBridge is IL1ERC20Bridge {
    event ERC20DepositInitiated(
        address indexed _l1Token,
        address indexed _l2Token,
        address indexed _from,
        address _to,
        uint256 _amount,
        bytes _data
    );

    mapping(address addr => uint256 balance) public l2Balances;

    function depositERC20ToByChainId(
        uint256,
        address _l1Token,
        address _l2Token,
        address _to,
        uint256 _amount,
        uint32,
        bytes calldata _data
    ) external payable override {
        require(msg.value > 0, "bridge fee required");
        IERC20(_l1Token).transferFrom(msg.sender, address(this), _amount);
        l2Balances[_to] += _amount;

        emit ERC20DepositInitiated(
            _l1Token,
            _l2Token,
            msg.sender,
            _to,
            _amount,
            _data
        );
    }
}

